import React , { Component } from 'react';
import JSON from '../json/Movies.json';

class Films extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      movies : JSON
    }
  }
  render() {

    const films = this.state.movies.map(movie => {
      return (
        <div>
          <h3>{movie.id} {movie.title}</h3>

        </div> 
    )
    })
    return (
      <div>
        {films}
      </div>
    );
  }
}

export default Films;
